mapper.select:
update product_info
product_name=#{productName}, product_sub_title=#{productSubTitle}, product_price=#{productPrice}, product_promote_price=#{productPromotePrice}, product_integral=#{productIntegral}, product_stock=#{productStock}, product_reviews_count=#{productReviewsCount}, product_total_selled=#{productTotalSelled}, picture_id=#{pictureId}, product_status=#{productStatus}, category_type=#{categoryType}
product_id=#{productId}

mapper.update:
update product_info set product_status=#{productStatus}
where
product_id in 

	#{productId}
